import React, { useEffect, useState } from "react";
import ForceGraph2D from "react-force-graph-2d";

function AnalyticsPage() {
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:8000/graph")
      .then(res => res.json())
      .then(data => {
        setGraphData({
          nodes: data.nodes,
          links: data.edges,
        });
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Caricamento grafo...</div>;

  return (
    <div style={{ height: "100vh", width: "100vw" }}>
      <ForceGraph2D
        graphData={graphData}
        nodeId="id"
        linkSource="source"
        linkTarget="target"
        nodeLabel="id"
        nodeAutoColorBy="id"
        enableNodeDrag={true}
        enableZoomPanInteraction={true}
        zoom={1}
        minZoom={0.1}
        maxZoom={10}
      />
    </div>
  );
}

export default AnalyticsPage;
