import React, { useEffect, useState } from "react";
import ForceGraph2D from "react-force-graph-2d";
import axios from "axios";

function App() {
  const [filter, setFilter] = useState("network1");
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });

  useEffect(() => {
    axios
      .get(`http://localhost:8000/networks?filter=${filter}`)
      .then((res) => {
        setGraphData(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, [filter]);

  return (
    <div style={{ padding: 20 }}>
      <h1>Dashboard Network</h1>

      <label>
        Seleziona filtro:{" "}
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="network1">Network 1</option>
          <option value="network2">Network 2</option>
        </select>
      </label>

      <div style={{ border: "1px solid #ccc", marginTop: 20, height: "600px" }}>
        <ForceGraph2D
          graphData={graphData}
          nodeAutoColorBy="id"
          linkDirectionalParticles={2}
          linkDirectionalParticleSpeed={0.005}
        />
      </div>
    </div>
  );
}

export default App;
