import React, { useEffect, useState } from "react";
import { Loader2, Network, Filter, BarChart3 } from "lucide-react";

// Simulate ForceGraph2D component since it's not available
const ForceGraph2D = ({ graphData, nodeColor, backgroundColor, nodeCanvasObject, ...props }) => {
  return (
    <div className="w-full h-full bg-gray-50 rounded-lg border-2 border-dashed border-gray-300 flex items-center justify-center">
      <div className="text-center">
        <Network className="w-16 h-16 text-gray-400 mx-auto mb-4" />
        <p className="text-gray-600 font-medium">Graph Visualization</p>
        <p className="text-sm text-gray-500 mt-2">
          {graphData.nodes.length} nodes, {graphData.links.length} links
        </p>
        <div className="mt-4 flex flex-wrap gap-2 justify-center max-w-md">
          {graphData.nodes.slice(0, 10).map((node, idx) => (
            <div
              key={idx}
              className="px-3 py-1 rounded-full text-xs font-medium text-white shadow-sm"
              style={{ backgroundColor: node.color }}
            >
              {node.id}
            </div>
          ))}
          {graphData.nodes.length > 10 && (
            <div className="px-3 py-1 rounded-full text-xs font-medium bg-gray-400 text-white">
