import React from "react";
import ForceGraph2D from "react-force-graph-2d";
import { FaNetworkWired } from "react-icons/fa"; // icona rete complessa

// Dati esempio per il grafico
const data = {
  nodes: [
    { id: "A" },
    { id: "B" },
    { id: "C" },
  ],
  links: [
    { source: "A", target: "B" },
    { source: "A", target: "C" },
  ],
};

export default function App() {
  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" }}>

      {/* Sidebar sinistra */}
      <aside style={{
        width: "220px",
        backgroundColor: "#202123",
        color: "white",
        display: "flex",
        flexDirection: "column",
        padding: "1rem",
      }}>
        <h2 style={{marginBottom: "2rem", fontWeight: "bold"}}>Menu</h2>
        <nav>
          <ul style={{ listStyle: "none", padding: 0 }}>
            <li style={{
              padding: "10px 15px",
              borderRadius: "8px",
              backgroundColor: "#343541",
              cursor: "pointer",
              marginBottom: "10px",
              fontWeight: "bold"
            }}>
              Dashboard
            </li>
            {/* Qui potrai aggiungere altre pagine */}
          </ul>
        </nav>
      </aside>

      {/* Main content area */}
      <div style={{ flexGrow: 1, display: "flex", flexDirection: "column" }}>

        {/* Topbar */}
        <header style={{
          height: "50px",
          backgroundColor: "#343541",
          color: "white",
          display: "flex",
          alignItems: "center",
          padding: "0 20px",
          boxShadow: "0 1px 4px rgba(0,0,0,0.2)"
        }}>
          <FaNetworkWired size={24} />
        </header>

        {/* Dashboard content */}
        <main style={{ flexGrow: 1, backgroundColor: "#444654", padding: "1rem" }}>
          <h1 style={{ color: "white" }}>Dashboard</h1>
          <ForceGraph2D
            graphData={data}
            nodeAutoColorBy="id"
            linkDirectionalParticles={2}
            linkDirectionalParticleSpeed={0.005}
            backgroundColor="#444654"
            nodeLabel="id"
            nodeRelSize={8}
          />
        </main>
      </div>
    </div>
  );
}
