import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { FaNetworkWired } from "react-icons/fa";
import Dashboard from "./Dashboard";

export default function App() {
  return (
    <Router>
      <div style={{ display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" }}>
        {/* Sidebar */}
        <aside style={{
          width: "220px",
          backgroundColor: "#202123",
          color: "white",
          display: "flex",
          flexDirection: "column",
          padding: "1rem",
        }}>
          <h2 style={{ marginBottom: "2rem", fontWeight: "bold" }}>Menu</h2>
          <nav>
            <ul style={{ listStyle: "none", padding: 0 }}>
              <li style={{ marginBottom: "10px" }}>
                <Link 
                  to="/" 
                  style={{ 
                    textDecoration: "none", 
                    color: "white", 
                    padding: "10px 15px", 
                    display: "block", 
                    borderRadius: "8px",
                    backgroundColor: "#343541",
                    fontWeight: "bold"
                  }}
                >
                  Dashboard
                </Link>
              </li>
              {/* Altri link pagine in futuro */}
            </ul>
          </nav>
        </aside>

        {/* Content area */}
        <div style={{ flexGrow: 1, display: "flex", flexDirection: "column" }}>
          {/* Topbar */}
          <header style={{
            height: "50px",
            backgroundColor: "#343541",
            color: "white",
            display: "flex",
            alignItems: "center",
            padding: "0 20px",
            boxShadow: "0 1px 4px rgba(0,0,0,0.2)"
          }}>
            <FaNetworkWired size={24} />
          </header>

          {/* Main Content: React Router Outlet */}
          <main style={{ flexGrow: 1, backgroundColor: "#444654", padding: "1rem" }}>
            <Routes>
              <Route path="/" element={<Dashboard />} />
              {/* Altre rotte in futuro */}
            </Routes>
          </main>
        </div>
      </div>
    </Router>
  );
}
